package service; import model.QuizItem; import java.text.Normalizer; public class AnswerChecker { public String normalize(String s){ if(s==null) return ""; String t=s.trim(); t=Normalizer.normalize(t, Normalizer.Form.NFKC); t=t.replaceAll("\\s+"," "); return t.toLowerCase(); } public boolean check(QuizItem item, String input){ return normalize(item.getAnswer()).equals(normalize(input)); } }